import { Injectable } from '@angular/core';
import { Color } from '../model/color';

@Injectable()
export class Extensions {
  ContiansItem<T>(
    lst: T[],
    obj: T,
    equalityFunction: (a: T, b: T) => boolean
  ): boolean {
    if (lst && lst.length > 0) {
      return lst.filter((k) => equalityFunction(k, obj)).length > 0;
    }
    return false;
  }

  rgba2hex(orig: Color): string {
    let r: string = String(orig.r);
    let g: string = String(orig.g);
    let b: string = String(orig.b);
    let a = '0.8';
    r = Number(r).toString(16);
    g = Number(g).toString(16);
    b = Number(b).toString(16);
    a = Math.round(Number(a) * 255).toString(16);

    if (r.length === 1) {
      r = '0' + r;
    }
    if (g.length === 1) {
      g = '0' + g;
    }
    if (b.length === 1) {
      b = '0' + b;
    }
    if (a.length === 1) {
      a = '0' + a;
    }

    return '#' + r + g + b; // + a;
  }

  beep() {
    const snd = new Audio(
      'data:audio/wav;base64,'
    );
    snd.play();
  }

  ToBoolean(value: string): boolean {
    return value === 'true';
  }

  GetRelativeDate(value: Date): any {
    let isInRange = false;
    const currentDate = new Date();
    if (
      currentDate.getFullYear() === value.getFullYear() &&
      currentDate.getMonth() === value.getMonth() &&
      (currentDate.getDate() - 1 === value.getDate() ||
        currentDate.getDate() === value.getDate() ||
        currentDate.getDate() + 1 === value.getDate())
    ) {
      isInRange = true;
      return {
        isInRange,
        parseDate: () => {
          const dif = currentDate.getDate() - value.getDate();
          if (dif === 0) {
            return `Today @ ${this.GetFormattedTimeStamp(value)}`;
          } else if (dif === 1) {
            return `Yesterdy @ ${this.GetFormattedTimeStamp(value)}`;
          } else if (dif === -1) {
            return `Tomorrow @ ${this.GetFormattedTimeStamp(value)}`;
          }
        },
      };
    } else {
      return { isInRange, parseDate: () => {} };
    }
  }

  private GetFormattedTimeStamp(value: Date): string {
    const hh = value.getHours();
    const mm = value.getMinutes();
    if (hh > 12) {
      return `${this.AppendZero(hh - 12)}:${this.AppendZero(mm)} PM`;
    } else if (hh === 12) {
      return `${this.AppendZero(hh)}:${this.AppendZero(mm)} PM`;
    } else {
      return `${this.AppendZero(hh === 0 ? 12 : hh)}:${this.AppendZero(mm)} AM`;
    }
  }

  private AppendZero = (val: number): string => {
    if (val < 10) {
      return `0${val}`;
    } else {
      return `${val}`;
    }
  }
}
